三角角度计算import java.util.Scanner;import java.lang.*;public class Main&#123;    public static class Triangle&#123;        double x1,y1,x2,y2,x3,y3;        double a_length,b_length,c_length,average;        double a_angle, b_angle, c_angle;        public void cal_len()&#123;            a_length = Math.sqrt(Math.pow((x2 - x3),2) + Math.pow((y2-y3),2));            b_length = Math.sqrt(Math.pow((x1 - x3),2) + Math.pow((y1-y3),2));            c_length = Math.sqrt(Math.pow((x1 - x2),2) + Math.pow((y1-y2),2));        &#125;        public void cal_angel()&#123;            a_angle=Math.toDegrees(Math.acos((Math.pow(a_length, 2) - Math.pow(b_length, 2)- Math.pow(c_length, 2))/(-2 * b_length * c_length)));            b_angle=Math.toDegrees(Math.acos((Math.pow(b_length, 2) - Math.pow(a_length, 2)- Math.pow(c_length, 2))/(-2 * a_length * c_length)));            c_angle=Math.toDegrees(Math.acos((Math.pow(c_length, 2) - Math.pow(b_length, 2)- Math.pow(a_length, 2))/(-2 * b_length * a_length)));        &#125;    &#125;    public static void main(String[] args)    &#123;        Scanner sc = new Scanner(System.in);        Triangle first = new Triangle();        System.out.println(&quot;输入三角形顶点A的坐标: &quot;);        first.x1 = sc.nextDouble();        first.y1 = sc.nextDouble();        System.out.println(&quot;输入三角形顶点B的坐标: &quot;);        first.x2 = sc.nextDouble();        first.y2 = sc.nextDouble();1        System.out.println(&quot;输入三角形顶点C的坐标: &quot;);        first.x3 = sc.nextDouble();        first.y3 = sc.nextDouble();        first.cal_len();        first.cal_angel();        System.out.printf(&quot;角A的角度为%.2f\n&quot; , first.a_angle);        System.out.printf(&quot;角B的角度为%.2f\n&quot; , first.b_angle);        System.out.printf(&quot;角C的角度为%.2f\n&quot; , first.c_angle);    &#125;&#125;





排序bubuleimport java.util.ArrayList;public class bubule &#123;	public static int size_ = 10;	public static int[] sort(int[] arr) &#123;		for(int i = 0; i &lt; arr.length; ++i) &#123;			for(int j = 0; j &lt; (arr.length - i); ++j) &#123;				if(arr[i] &lt; arr[j]) &#123;					arr[i] += arr[j];					arr[j] = arr[i] - arr[j];					arr[i] = arr[i] - arr[j];				&#125;			&#125;			&#125;			return arr;	&#125;	public static void print(int[] arr) &#123;		for(int i = 0; i &lt; arr.length; ++i)			System.out.print(arr[i] + &quot; &quot;);		System.out.println();	&#125;	public static void main(String[] args) &#123;		int[] arr1 = new int[size_];		int[] arr2 = null;		for(int i = 0; i &lt; size_; ++i)			arr1[i] = (int)(100 +Math.random() * (100 + 1));		System.out.println(&quot;before order:&quot;);		print(arr1);					arr2 = sort(arr1);		System.out.println(&quot;after order:&quot;);		print(arr2);	&#125;&#125;;



insertpublic class insert &#123;	public static int size_ = 10;	public static int[] sort(int[] arr) &#123;		for(int i = 1; i &lt; arr.length; ++i) &#123;			int t = arr[i];	// unorder arr			int j;			// move arr to back			for(j = i - 1; j &gt;= 0 &amp;&amp; t &lt; arr[j]; j--)				arr[j + 1] = arr[j];			arr[j + 1] = t;		&#125;		return arr;	&#125;	public static void print(int[] arr) &#123;		for(int i = 0; i &lt; arr.length; ++i)				System.out.print(arr[i] + &quot; &quot;);		System.out.println();	&#125;	public static void main(String[] args) &#123;		int[] arr1 = new int[size_];					int[] arr2 = null;		for(int i = 0; i &lt; arr1.length; ++i)			arr1[i] = (int)(Math.random() * (100 + 1));		System.out.println(&quot;before sort&quot;);		print(arr1);		arr2 = sort(arr1);		System.out.println(&quot;after sort&quot;);		print(arr2);	&#125;&#125;;

