st_mtime;     //最后一次修改时间	 * time_t st_ctime      //最后一次改变时间	 * &#125;;	 * */	struct stat st;	int fd = -1;	fd = open(&quot;Logan.txt&quot;,O_RDWR);	if(fd == -1)	&#123;		perror(&quot;open file&quot;);		exit(1);	&#125;	fstat(fd,&amp;st);		//获取文件大小	printf(&quot;Size of file %d\n&quot;,st.st_size);&#125;void sys_04() //access()&#123;	//int access(const char *pathname,int mode);	//测试指定文件是否拥有某种权限		int ret = access(&quot;Logan.txt&quot;,W_OK);	if(ret == -1)	&#123;		perror(&quot;access&quot;);		exit(1);	&#125;	printf(&quot;you can write this file.\n&quot;);&#125;//实现一个chmod 命令void sys_05(int argc,char **argv) //chmod()&#123;	//strtol(const char *nptr,char **endptr, int base); //**endptr 通常为NULL	int ret = chmod(argv[2],strtol(argv[1],NULL,8)); //八进制	if(argc &lt; 3)	&#123;		printf(&quot;Useage: ./a.out 755 filename&quot;);	&#125;	if(ret == -1)	&#123;		perror(&quot;Chmod&quot;);		exit(1);	&#125;	printf(&quot;Mode changed!\n&quot;);	//strtol&#125;void sys_06(int argc,char **argv) //chown()&#123;	 /*	  * int chown(const char *pathname, uid_t owner,gid_t group);	  * int fchown(int fd,uid_t owner,gid_t group);	  * int lchown(const char *pathname,uid_t owner,gid_t group);	  * uid_t &amp;&amp; gid_t 可通过 etc/passwd 获取	  * 查看配置文件  man 5 passwd	  * */&#125;void sys_07() //truncate()&#123;	/*	 * int truncate(const char *path, off_t length);	 * int ftruncate(int fd, off_t length);	 * */	int trun = truncate(&quot;Logan.txt&quot;,12);	if(trun == -1)	&#123;		perror(&quot;truncate&quot;);		exit(1);	&#125;	printf(&quot;OK!&quot;);&#125;void sys_08()&#123;	/*创建一个硬链接	 *int link(const char *oldpath,const char *newpath);	 *创建软链接	 *syslink()	 *读软链接对应的文件名,不是内容	 *readlink()	 *	 *删除一个文件的目录项并减少它的链件数,若成功则返回0,否则返回1	 *如果想通过这个函数来成功删除文件,你就必须拥有这个目录的可执行和写权限	 *unlink()	 * */&#125;void sys_09()//rename() 更改名字函数&#123;	//rename()	//renameat(int olddirfd,const char *oldpath,int newdirfd,const char *newpath);		//renameat2(int olddirfd ,const char *oldpath, int newdirfd, const char *newpath, unsigned int flags);	int ret = rename(&quot;./Logan.txt&quot;,&quot;OK.txt&quot;);	if (ret == -1)	&#123;		perror(&quot;rename&quot;);		exit(1);	&#125;	printf(&quot;OK!&quot;);&#125;void sys_10(int argc,char **argv) //目录操作函数&#123;	//chdir()   改变目录 -- int chdir(const char *path);	//getcwd()  获取路径	//mkdir()   创建目录	//rmdir()   删除空目录	//opendir   打开一个目录 打开成功非0 失败返回 NULL	//	//readdir   读目录	//struct dirent *readdir(DIR *dirp);	//	//返回值:	//struct dirent	//&#123;	//	ino_t dino;    //此目录进入点的inode	//	ff_t  d_off;   //目录文件开头至此目录进入点的位移	//	signed short int d_reclen; //d_name的长度,不包含NULL字符	//	unsigned char d_type;	//d_name所指的文件类型	//	har d_name[256];	//文件名	//&#125;	//d_type	//DT_BLK -块设备	//DT_CHR -字符设备	//DT_DIR -目录	//DT_LNK -软链接	//DT_FIFO -管道	//DT_REG  -普通文件	//DT_SOCK - 套接字	//DT_UNKNOWN -未知	//	//-D_BSD_SOURCE 编译时添加的宏定义	//	//	//closedir  关闭目录	//	char path[256] = &#123;0&#125;;	if(argc &lt; 2)	&#123;		printf(&quot;Useage: ./a.out path&quot;);		exit(1);	&#125;	int ret = chdir(argv[1]);	if(ret == -1)	&#123;		perror(&quot;chdir&quot;);		exit(1);	&#125;	getcwd(path, sizeof(path));	printf(&quot;current path: %s\n&quot;,path);&#125;//使用readdir函数来获取当前普通文件个数实例int getFileNum(char *root)&#123;	DIR* dir = NULL;	dir = opendir(root);	if(dir == NULL)	&#123;		perror(&quot;opendir&quot;);		exit(1);	&#125;	//便利目录	struct dirent* ptr = NULL;	char path_name[1024] = &#123;0&#125;;	int total = 0;	while( (ptr = readdir(dir)) )	&#123;		//过滤. 和 ..		if(strcmp(ptr-&gt;d_name,&quot;.&quot;) == 0 || strcmp(ptr-&gt;d_name,&quot;..&quot;) == 0)		&#123;			continue;		&#125;			//如果是目录		if(ptr-&gt;d_type == DT_DIR)		&#123;			//递归 读目录			sprintf(path_name,&quot;%s/%s&quot;,root,ptr-&gt;d_name);			total += getFileNum(path_name);		&#125;			//如果是普通文件		if(ptr-&gt;d_type == DT_REG)		&#123;			total++;		&#125;	&#125;	closedir(dir);	return total;&#125;void sys_11(int argc,char **argv)&#123;	if(argc &lt; 2)	&#123;		printf(&quot;Usage:./a.out filepath\n&quot;);		exit(1);	&#125;	printf(&quot;Number of Curent file:%d\n&quot;,getFileNum(argv[1]));&#125;void sys_12()&#123;	/*文件描述符的复制	 *	 *dup(),dup2()	 *dup()返回一个最小的未被占用的文件描述符号	 *dup2() dup2(int odfd,int newfd);	 *打开文件描述符前,若newfd已经存在先关闭newfd,然后再拷贝	 *若oldfd与newfd是同一个文件描述符,则不会关闭newfd	 *	 * */	int fd = open(&quot;Logan.txt&quot;,O_RDWR);	if(fd == -1)	&#123;		perror(&quot;open&quot;);		exit(1);	&#125;	printf(&quot;file open fd = %d\n&quot;,fd);		//找到进程文件描述表中 第一个 可用的文件描述符	//将参数指定的文件复制到该描述符后,返回这个描述符	int ret = dup(fd);	if(ret == -1)	&#123;		perror(&quot;dup&quot;);		exit(1);	&#125;	printf(&quot;dup fd = %d\n&quot;,ret);	char* buf = &quot;你是猴子派来的救兵吗?\n&quot;;	char *buf_2 = &quot;你大爷的! 我是程序猿!!\n&quot;;	write(fd, buf,strlen(buf));	write(ret, buf_2,strlen(buf_2));	close(fd);	close(ret);&#125;void sys_13()&#123;	//fcntl	//	//int fcntl(int fd, int cmd)	//int fcntl(int fd, int cmd, long arg)  (目前使用)	//int fcntl(int fd, int cmd, struct flock *lock)	//	//复制现有的文件描述符 F_DUPFD	//获得/设置文件描述符标记 --cmd F_GETFD / F_SETFD	//	//获得/设置文件状态标记   --CMD 	//  ***F——GETFL	//只读打开:  O_RDONLY	//只写打开:  O_WRONLY	//读写打开:  O_RDWR	//执行打开:  O_EXEC	//搜索打开目录:  O_SEARCH	//追加写:    A_PPEND	//非阻塞模式: O_NONBLOCK	//  ***F_SETFL	//O_APPEND	//O_NONBLOCK	//	//获得/设置异步I/O所有权 --cmd  F_GETOWN / F_SETOWN	//获得/设置记录锁 --cmd F_GETLK / F_SETLK / F_SETLKW		char *txt1 = &quot;我是&quot;	char *txt2 = &quot;Logan&quot;	int fd = open(&quot;Logan.txt&quot;,O_WRONLY);	if(fd == -1)		&#123;		perror(&quot;open&quot;);		exit(1);	&#125;	if(write(fd,txt1,strlen(txt1)) == -1)	&#123;		perror(&quot;write&quot;);		eixt(1);	&#125;	int flag = fcntl(fd, F_GETFL, 0);&#125;int main(int argc,char **argv)&#123;	//sys_01();	//sys_02();	//sys_03();	//sys_04();	//sys_05(argc,argv);	//sys_07();	//sys_09();	//sys_10(argc,argv);	//sys_11(argc,argv);	sys_12();	return 0;&#125;



实现 ls#include &lt;sys/types.h&gt; #include &lt;sys/stat.h&gt; #include &lt;stdio.h&gt;#include &lt;stdlib.h&gt; #include &lt;time.h&gt; #include &lt;pwd.h&gt; #include &lt;grp.h&gt; #include &lt;string.h&gt;int main(int argc,char *argv[])&#123;	if(argc &lt; 2)	&#123;		printf(&quot;Useage: ./a.out filename\n&quot;);		exit(1);	&#125;	struct stat st;	int ret = stat(argv[1],&amp;st);	if(ret == -1)	&#123;		perror(&quot;stat&quot;);		exit(1);	&#125;	//储存文件类型和访问权限	char perms[11] = &#123;0&#125;;	//判断文件类型	switch(st.st_mode &amp; S_IFMT)	&#123;		case S_IFLNK:			perms[0] = &#x27;1&#x27;;			break;		case S_IFDIR:			perms[0] = &#x27;d&#x27;;			break;		case S_IFREG:			perms[0] = &#x27;-&#x27;;			break;		case S_IFBLK:			perms[0] = &#x27;b&#x27;;			break;		case S_IFCHR:			perms[0] = &#x27;c&#x27;;			break;		case S_IFSOCK:			perms[0] = &#x27;s&#x27;;			break;		case S_IFIFO:			perms[0] = &#x27;P&#x27;;			break;		default:			perms[0] = &#x27;?&#x27;;			break;			&#125;	//判断文件的访问权限	//文件所有者	perms[1] = (st.st_mode &amp; S_IRUSR) ? &#x27;r&#x27; : &#x27;-&#x27;;	perms[2] = (st.st_mode &amp; S_IWUSR) ? &#x27;w&#x27; : &#x27;-&#x27;;	perms[3] = (st.st_mode &amp; S_IXUSR) ? &#x27;x&#x27; : &#x27;-&#x27;;	//文件所属组	perms[4] = (st.st_mode &amp; S_IRGRP) ? &#x27;r&#x27; : &#x27;-&#x27;;	perms[5] = (st.st_mode &amp; S_IWGRP) ? &#x27;w&#x27; : &#x27;-&#x27;;	perms[6] = (st.st_mode &amp; S_IXGRP) ? &#x27;x&#x27; : &#x27;-&#x27;;	//其他人	perms[7] = (st.st_mode &amp; S_IROTH) ? &#x27;r&#x27; : &#x27;-&#x27;;	perms[8] = (st.st_mode &amp; S_IWOTH) ? &#x27;w&#x27; : &#x27;-&#x27;;	perms[9] = (st.st_mode &amp; S_IXOTH) ? &#x27;x&#x27; : &#x27;-&#x27;;	//硬件链接计数	int linkNum = st.st_nlink;	//文件所有者	char* fileUser = getpwuid(st.st_uid)-&gt;pw_name;	//文件所属组	char* fileGrp = getgrgid(st.st_gid)-&gt;gr_name;	//文件大小	int fileSize = (int)st.st_size;	//修改时间	char* time = ctime(&amp;st.st_mtime);	char mtime[512] = &#123;0&#125;;	strncpy(mtime, time, strlen(time)-1);		char buf[1024];	sprintf(buf,&quot;%s %d %s %s %d %s %s&quot;,perms,linkNum,fileUser,fileGrp,fileSize,mtime,argv[1]);	printf(&quot;%s\n&quot;,buf);	return 0;&#125;

