#include &lt;sys/socket.h&gt;#include &lt;netinet/in.h&gt;#include &lt;arpa/inet.h&gt;#include &lt;signal.h&gt;#include &lt;unistd.h&gt;#include &lt;assert.h&gt;#include &lt;string.h&gt;#include &lt;stdio.h&gt;#include &lt;stdlib.h&gt;static bool stop = false;static void handle_term(int sig) &#123;	stop = true;&#125;int main(int argc, char **argv) &#123;	signal(SIGTERM, handle_term);	if(argc &lt;= 3) &#123;		printf(&quot;Usage: %s ip_address port_number backlog\n&quot;, basename(argv[0]));		return 1;	&#125;	const char *ip = argv[1];	int port = atoi(argv[2]);	int backlog = atoi(argv[3]);	int sock = socket(PF_INET, SOCK_STREAM, 0);	assert(sock &gt;= 0);	struct sockaddr_in address;	bzero(&amp;address, sizeof(address)); //清空为0	address.sin_family = AF_INET;	//inet_pton是一个IP地址转换函数,	//可以在将IP地址在“点分十进制”和“二进制整数”之间转换	//而且inet_pton和inet_ntop这2个函数能够处理ipv4和ipv6		inet_pton(AF_INET, ip,  &amp;address.sin_addr);	address.sin_port = htons(port); //转化为网络字节序	//绑定	int ret = bind(sock, (struct sockaddr *)&amp;address, sizeof(address));	assert(-1 != ret);	ret = listen(sock, backlog);	assert(-1 != ret);	//通过系统发送信号来终止程序	while(!stop) &#123;		sleep(1);	&#125;	close(sock);	printf(&quot;End program\n&quot;);	return 0;&#125;



获取客户端基本信息#include &lt;unistd.h&gt;#include &lt;stdio.h&gt;#include &lt;sys/socket.h&gt;#include &lt;netinet/in.h&gt;#include &lt;arpa/inet.h&gt;#include &lt;assert.h&gt;#include &lt;errno.h&gt;#include &lt;string.h&gt;#include &lt;stdlib.h&gt;int main(int argc, char **argv) &#123;	if(argc &lt;= 2) &#123;		printf(&quot;Usage: %s [ip] [port]\n&quot;, basename(argv[0]));		return 1;	&#125;	const char *ip = argv[1];	int port = atoi(argv[2]);	struct sockaddr_in address;	bzero(&amp;address, sizeof(address));	address.sin_family = AF_INET;	inet_pton(AF_INET, ip, &amp;address.sin_addr);	address.sin_port = htons(port);	int sock = socket(PF_INET, SOCK_STREAM, 0);	assert(sock &gt;= 0);	int ret = bind(sock, (struct sockaddr *)&amp;address, sizeof(address));	if(-1 == ret) &#123;		perror(&quot;bind&quot;);		return 1;	&#125;	ret = listen(sock, 5);	assert(ret != -1);	struct sockaddr_in client_addr;	socklen_t client_addr_len = sizeof(client_addr);	int c_fd = accept(sock, (struct sockaddr*)&amp;client_addr, &amp;client_addr_len);	if(c_fd == -1) &#123;		perror(&quot;accept::&quot;);	&#125;else &#123;		char remote[INET_ADDRSTRLEN];		printf(&quot;connected with ip: %s and port %d\n&quot;,		inet_ntop(AF_INET, &amp;client_addr.sin_addr, remote, INET_ADDRSTRLEN),		ntohs(client_addr.sin_port));		close(c_fd);	&#125;	return 0;&#125;



