#include &lt;iostream&gt;#include &lt;cstdio&gt;#include &lt;cstring&gt;#include &lt;cstdlib&gt;#include &lt;string&gt;#include &lt;unistd.h&gt;#include &lt;sys/socket.h&gt;#include &lt;netinet/in.h&gt;#include &lt;arpa/inet.h&gt;using namespace std;#define SERVER_PORT 6666#define SERVER_IP &quot;127.0.0.1&quot;int main(int argc, char **argv) &#123;	int sockfd;	int n;	struct sockaddr_in serveraddr;	char str[256] = &quot;Hello You guys\n&quot;;	if(argc &lt; 2) &#123;		cout &lt;&lt; &quot;Usage: ./clinet [message]&quot; &lt;&lt; endl;			fputs(&quot;Input error! \n&quot;, stderr);		exit(1);	&#125;	sockfd = socket(AF_INET, SOCK_STREAM, 0);	memset(&amp;serveraddr, 0, sizeof(struct sockaddr_in));	serveraddr.sin_family = AF_INET;	inet_pton(AF_INET, SERVER_IP, &amp;serveraddr.sin_addr);	serveraddr.sin_port = htons(SERVER_PORT); //转化为网络字节顺序		connect(sockfd, (struct sockaddr *)&amp;serveraddr, sizeof(serveraddr));	write(sockfd, argv[1], strlen(argv[1]));	n = read(sockfd, str, sizeof(str) - 1);	if(n &gt; 0) &#123;		str[n] = &#x27;\0&#x27;;		cout &lt;&lt; str;	&#125;else &#123;		perror(&quot;error&quot;);	&#125;		cout &lt;&lt; endl &lt;&lt; &quot;finished&quot; &lt;&lt; endl;	close(sockfd);	return 0;&#125;

服务端#include &lt;stdio.h&gt;#include &lt;unistd.h&gt;#include &lt;sys/types.h&gt;#include &lt;sys/socket.h&gt;#include &lt;string.h&gt;#include &lt;ctype.h&gt;#include &lt;arpa/inet.h&gt;#define SERVER_PORT 6666int main(void) &#123;	int sock;  //信箱	//AF_INET表示使用网络地址, SOCK_STREAM表示使用TCP协议	sock = socket(AF_INET, SOCK_STREAM, 0);	struct sockaddr_in server_addr; //这个结构体,头文件中已经声明	//清空标签	bzero(&amp;server_addr, sizeof(server_addr));	//设置协议家族为网络协议		//htonl = host to net ,选择协议族为ipv4,	server_addr.sin_family = AF_INET;	//监听本地所有地址	server_addr.sin_addr.s_addr = htonl(INADDR_ANY);	server_addr.sin_port = htons(SERVER_PORT); //绑定端口号	//实现标签贴到信箱上	bind(sock, (struct sockaddr *)&amp;server_addr, sizeof(server_addr));	listen(sock, 128); //同时的连接数量,同时来的信件接受多少封,这里为128		//万事俱备,只等来信.	printf(&quot;等待客户连接...\n&quot;);	while(1) &#123;		struct sockaddr_in client;		int client_sock ; //客户端		char client_ip[64];		char buf[256];		int buf_len;		socklen_t client_addr_len = sizeof(client);		 client_sock = accept(sock, (struct sockaddr *)&amp;client, &amp;client_addr_len);		//打印客户端地址和端口号			//转化网络字节序		printf(&quot;client ip: %s\n port: %d\n&quot;, 		inet_ntop(AF_INET, &amp;client.sin_addr.s_addr, client_ip, sizeof(client_ip)),		ntohs(client.sin_port));				//读取数据		buf_len = read(client_sock, buf, sizeof(buf) - 1);		buf[buf_len] = &#x27;\x00&#x27;;		printf(&quot;接受到消息: %s\n&quot;, buf);		for(int i = 0; i &lt; buf_len; i++) &#123;				buf[i] = toupper(buf[i]);		&#125;			write(client_sock, buf, strlen(buf));		printf(&quot;write[%s] finished!!!\n&quot;, buf);		close(client_sock);	&#125;	return 0;&#125;

